package Even_Odd;

public class SumOfArray
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int array[] = {10,20,30};
		int sum = 0;
		
		System.out.print("Sum of array elements=10+20+30=");
		for(int i = 0;i<3;i++)
		{
			sum = sum+array[i];	
		}
			System.out.println(sum);	
	}

}
